import React, { Component, useState } from 'react';

export class Clock extends Component{
    constructor(props) {
        super(props)
        useState({
            date: new Date()
        })
        // this.state = {
        //     date : new Date()
        // }
        this.timer = null
    }

    componentDidMount(){
        this.timer = window.setInterval(()=> {
            useState({
                date: new Date()
            })
            // this.setState({
            //     date: new Date()
            // })
        })
    }
    
    componentWillUnmount(){
        window.clearInterval(this.timer)
    }

    render(){
        return <div>
            {this.state.date.toLocaleString()}
        </div>
    }
}
